SELECT ref_g.SA1_MAIN16, shapes.*, st_area(ref_g.geometry)*12391.3 as ref_area, st_area(shapes.geometry)*12391.3 as shp_area, shapes.geometry FROM ref_g,shapes WHERE ST_Intersection(ref_g.geometry,shapes.geometry) or ST_Intersects(ref_g.geometry,shapes.geometry)or ST_Contains(ref_g.geometry,shapes.geometry) or ST_Intersects(ref_g.geometry,shapes.geometry) or ST_Within(ref_g.geometry,shapes.geometry) limit 100
